var nools = require ('nools');

var ruleFilePath = __dirname + '/mesrules.nools';
var flow = nools.compile(ruleFilePath);
var session = flow.getSession ();

var Output = flow.getDefined('output');

session.assert (new Output("kevin se bat chaque jour contre KFC pour faire le mort"));
session.assert (new Output("contre"));

session.match();